// src/cli.ts
var { build } = require("gluegun");
async function run(argv) {
  const cli = build().brand("shopware-pwa").src(__dirname).plugins("./node_modules", { matching: "shopware-pwa-*", hidden: true }).help().version().create();
  const toolbox = await cli.run(argv);
  return toolbox;
}
module.exports = { run };
