// src/commands/build.ts
var command = {
  name: "build",
  description: "Build your project for production",
  run: async (toolbox) => {
    const {
      system: { spawn },
      print: { info }
    } = toolbox;
    const devMode = !!toolbox.inputParameters.devMode;
    info(`Starting Shopware PWA project building (devMode: ${devMode ? "enabled" : "disabled"})...`);
    await toolbox.plugins.invokeRefreshPlugins(devMode);
    await toolbox.cms.invokeRefreshCMS();
    await toolbox.languages.invokeRefreshLanguages();
    const result = await spawn("yarn nuxt build", {
      stdio: "inherit"
    });
    if (result.status !== 0)
      throw new Error("Unable to build project");
  }
};
module.exports = command;
